SELECT properties.id, title, cost_per_night, start_date, AVG(rating) ASaverage_rating FROM reservations JOIN property_reviews ON property_reviews.property_id=reservations.property_id JOIN properties ON properties.id=reservations.property_id WHERE reservations.guest_id = 1 AND reservations.end_date < now()::date GROUP BY properties.id, reservations.id ORDER BY reservations.start_date LIMIT 10;